import { Component, AfterViewInit, ElementRef, Inject, PLATFORM_ID } from '@angular/core';
import { CommonModule, NgFor, isPlatformBrowser } from '@angular/common';
import { RouterLink } from '@angular/router';

@Component({
    selector: 'app-home',
    standalone: true,
    imports: [CommonModule, RouterLink, NgFor],
    templateUrl: './home.component.html',
    styleUrls: ['./home.component.css']
})
export class HomeComponent implements AfterViewInit {
  testimonials = [
    {
      quote: "El equipo de Grupo EMPATIC demostr칩 un compromiso y una pasi칩n que transformaron por completo nuestro proyecto. Su enfoque estrat칠gico y humano fue clave para el 칠xito.",
      author: "Ana Sof칤a",
      title: "Directora de Innovaci칩n, Corp Inc.",
      avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704d",
      ringColor: "ring-primary"
    },
    {
      quote: "La metodolog칤a de EMPATIC nos permiti칩 no solo alcanzar nuestras metas, sino superarlas. Su visi칩n para integrar la tecnolog칤a en el impacto social es 칰nica.",
      author: "Carlos Rodr칤guez",
      title: "Gerente de Sostenibilidad, Natura SAC",
      avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704e",
      ringColor: "ring-secondary"
    },
    {
      quote: "Trabajar con Grupo EMPATIC ha sido una experiencia inspiradora. Su dedicaci칩n a la formaci칩n de l칤deres y al desarrollo comunitario es genuina y efectiva.",
      author: "Luc칤a Mendoza",
      title: "L칤der de Proyecto, ONG Futuro",
      avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704f",
      ringColor: "ring-accent"
    },
    {
      quote: "La capacidad de Grupo EMPATIC para innovar y adaptarse a nuestras necesidades fue impresionante. Definitivamente un aliado estrat칠gico para cualquier empresa con prop칩sito.",
      author: "Javier G칩mez",
      title: "CEO, StartUp Creativa",
      avatar: "https://i.pravatar.cc/150?u=a042581f4e29026704g",
      ringColor: "ring-success"
    }
  ];

  constructor(
    private elementRef: ElementRef,
    @Inject(PLATFORM_ID) private platformId: Object
  ) {}

  ngAfterViewInit() {
    if (isPlatformBrowser(this.platformId)) {
      setTimeout(() => {
        const observer = new IntersectionObserver((entries) => {
          entries.forEach(entry => {
            if (entry.isIntersecting) {
              entry.target.classList.add('visible');
              entry.target.classList.remove('hidden-for-animation');
            }
          });
        }, {
          threshold: 0.1
        });

        const sections = this.elementRef.nativeElement.querySelectorAll('.hidden-for-animation');
        sections.forEach((section: any) => {
          observer.observe(section);
        });
      }, 100);
    }
  }
}

